import React, { useState } from 'react';
import './Expenses.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';

const Expense = props => {
	const [filteredYear, setFilteredYear] = useState('2020');

	const expense1 = [...props.items];

	const selecteditems = [];

	const filterChangeHandler = selectedYear => {
		console.log(selectedYear);
		expense1.map(expense => {
			if (expense.date.getFullYear() == Number(selectedYear)) {
				return (selecteditems = [expense, ...selecteditems]);
			}
		});
	};

	return (
		<div>
			<Card className="expenses">
				<ExpensesFilter
					selected={typeof filteredYear}
					onChangefilter={filterChangeHandler}
				/>
				{selecteditems.map(expense => {
					/* 
					不建議用index的方式，對於項目的順序數量總是一樣的。
					所以有可能刪除第二項目，第三項目就變成第二項目
					*/

					// if(expense.date.getFullYear()===);
					return (
						<ExpenseItem
							key={expense.id}
							title={expense.title}
							amount={expense.amount}
							date={expense.date}
						/>
					);
				})}
			</Card>
		</div>
	);
};

export default Expense;
